sqlMap.sql:
WITH t1 (sale_group2, pro_code, pro_name, sale_weight, sale_amt, total_weight, total_amt) AS ( select "FN_GET_TMCODEXDNM" (c.corp_code, 'SD025', c.sale_group2) sale_group2 ,c.pro_code ,c.pro_name ,SUM (case when c.sale_date = #lastDate# then c.sale_weight else 0 end) sale_weight ,SUM (case when c.sale_date = #lastDate# then c.sale_amt + c.sale_vat else 0 end) sale_amt ,sum(c.sale_weight) total_weight ,sum(c.sale_amt + c.sale_vat) total_amt from (select a.corp_code ,b.sale_weight ,b.sale_amt ,b.sale_vat ,a.part_code ,b.sale_date ,h.pro_code ,h.pro_name ,h.sale_group2 from mp_cust_hist a ,mp_cust_record b ,mp_cust_info e ,mp_item_master_m h where a.corp_code = #corpCode# and a.part_code = #teamCode# and a.salesman = #salesman# and to_char(now(), 'YYYYMMDD')::varchar between a.start_date and a.last_date and b.sale_date between substr(#strtDate#, 1, 6)||'01' and #lastDate# and a.corp_code = b.corp_code and a.cust_code = b.cust_code and a.corp_code = e.corp_code and a.cust_code = e.cust_code and b.corp_code = h.corp_code and b.pro_code = h.pro_code) c group by c.corp_code, c.sale_group2, c.pro_code, c.pro_name order by c.sale_group2, c.pro_code, c.pro_name
), goal (pro_code, target_weight) AS ( select h.pro_code ,sum(b.target_weight) target_weight from mp_cust_hist a ,mp_custgoal_yymm b ,mp_cust_info e ,mp_item_master_m h where a.corp_code = #corpCode# and a.part_code = #teamCode# and a.salesman = #salesman# and to_char(now(), 'YYYYMMDD')::varchar between a.start_date and a.last_date and b.target_yymm = substr(#strtDate#, 1, 6) and a.corp_code = b.corp_code and a.cust_code = b.cust_code and a.corp_code = e.corp_code and a.cust_code = e.cust_code and b.corp_code = h.corp_code and b.sale_group2 = h.sale_group2 group by h.sale_group2, h.pro_code
), t3 (pro_code, month_sync) AS ( select h.pro_code ,sum(b.sale_weight) month_sync from mp_cust_hist a ,mp_cust_record b ,mp_cust_info e ,mp_item_master_m h where a.corp_code = #corpCode# and a.part_code = #teamCode# and a.salesman = #salesman# and to_char(now(), 'YYYYMMDD')::varchar between a.start_date and a.last_date and b.sale_date between TO_CHAR(TO_DATE(#strtDate#, 'yyyymmdd') - INTERVAL '1 MONTH', 'YYYYMMDD') AND openmps."FN_GET_LASTDAY" (#lastDate#, 'm') and a.corp_code = b.corp_code and a.cust_code = b.cust_code and a.corp_code = e.corp_code and a.cust_code = e.cust_code and b.corp_code = h.corp_code and b.pro_code = h.pro_code group by h.pro_code ORDER BY 1), t4 (pro_code, yyyy_sync) AS ( select h.pro_code ,sum(b.sale_weight) month_sync from mp_cust_hist a ,mp_cust_record b ,mp_cust_info e ,mp_item_master_m h where a.corp_code = #corpCode# and a.part_code = #teamCode# and a.salesman = #salesman# and to_char(now(), 'YYYYMMDD')::varchar between a.start_date and a.last_date and b.sale_date between TO_CHAR(TO_DATE(#strtDate#, 'yyyymmdd') - INTERVAL '12 MONTH', 'YYYYMMDD') AND openmps."FN_GET_LASTDAY" (#lastDate#, 'y') and a.corp_code = b.corp_code and a.cust_code = b.cust_code and a.corp_code = e.corp_code and a.cust_code = e.cust_code and b.corp_code = h.corp_code and b.pro_code = h.pro_code group by h.pro_code ORDER BY 1) SELECT t1.sale_group2, t1.pro_code, t1.pro_name, t1.sale_weight, t1.sale_amt, t1.total_weight, t1.total_amt, goal.target_weight, t3.month_sync, t4.yyyy_sync FROM t1 LEFT OUTER JOIN goal ON t1.pro_code = goal.pro_code LEFT OUTER JOIN t3 ON t1.pro_code = t3.pro_code LEFT OUTER JOIN t4 ON t1.pro_code = t4.pro_code ORDER BY 1, 3, 4
